import { NextResponse } from 'next/server';
import { getDb } from '@/lib/db';

export async function PUT(request: Request, { params }: { params: { id: string } }) {
  try {
    const { id } = await params;
    const { name, category, price, description, image } = await request.json();
    const db = await getDb();
    await db.run(
        "UPDATE menu_items SET name = ?, category = ?, price = ?, description = ?, image = ? WHERE id = ?", 
        [name, category, price, description, image, id]
    );
    return NextResponse.json({ id, name, category, price, description, image });
  } catch (error: any) {
    return NextResponse.json({ error: error.message }, { status: 500 });
  }
}

export async function DELETE(request: Request, { params }: { params: { id: string } }) {
    try {
        const { id } = await params;
        const db = await getDb();
        await db.run("DELETE FROM menu_items WHERE id = ?", [id]);
        return NextResponse.json({ success: true });
    } catch (error: any) {
        return NextResponse.json({ error: error.message }, { status: 500 });
    }
}


import { NextResponse } from 'next/server';
import { getDb } from '@/lib/db';

export async function GET() {
  try {
    const db = await getDb();
    const orders = await db.all("SELECT * FROM orders ORDER BY created_at DESC");
    const parsedOrders = orders.map(row => {
      try {
        return {
          ...row,
          items: JSON.parse(row.items || '[]'),
          total: row.value // Map 'value' from DB to 'total' for frontend
        };
      } catch (e) {
        return { ...row, items: [], total: row.value };
      }
    });
    return NextResponse.json(parsedOrders);
  } catch (error: any) {
    return NextResponse.json({ error: error.message }, { status: 500 });
  }
}

export async function POST(request: Request) {
  try {
    const body = await request.json();
    const { 
      id = Date.now().toString(), 
      table_number, 
      items, 
      status = 'pending', 
      total = 0,
      name = `Mesa ${table_number}`,
      type = 'dine-in',
      payment_method
    } = body;

    const db = await getDb();
    const sql = `INSERT INTO orders (id, name, table_number, items, status, type, value, date, payment_method) VALUES (?, ?, ?, ?, ?, ?, ?, ?, ?)`;
    const date = new Date().toISOString();

    await db.run(sql, [
      id, 
      name, 
      table_number, 
      JSON.stringify(items), 
      status, 
      type, 
      total,
      date,
      payment_method
    ]);

    return NextResponse.json({ id, name, table_number, items, status, type, total, date, payment_method });
  } catch (error: any) {
    return NextResponse.json({ error: error.message }, { status: 500 });
  }
}

export async function PUT(request: Request) {
    try {
        // We need to parse the URL to get the ID if we are using dynamic routes, 
        // but this file is `app/api/orders/route.ts` which handles `/api/orders`.
        // To handle `/api/orders/[id]`, we should have created `app/api/orders/[id]/route.ts`.
        // My KitchenBoard calls `api.put('/orders/${orderId}', ...)`
        // This means it expects `/api/orders/123`.
        // Currently, `app/api/orders/route.ts` ONLY handles `/api/orders`.
        // It does NOT handle `/api/orders/123`.

        // CRITICAL FIX: I need to move this logic to `app/api/orders/[id]/route.ts` OR
        // change the frontend to call `PUT /api/orders` with `{ id, ... }`.

        // Changing the frontend is easier if I didn't want to create folders.
        // But `KitchenBoard` is already written to use RESTful ID in URL: `api.put(/orders/${orderId}, ...)`.
        // So I MUST create `app/api/orders/[id]/route.ts`.

        // However, I can also handle it here if I check the URL, but Next.js App Router separates them.
        // So I will create `app/api/orders/[id]/route.ts` for PUT/DELETE/GET-one.

        // Wait, for `POST` (create), it stays in `route.ts`.
        // For `GET` (list), it stays in `route.ts`.

        return NextResponse.json({ error: "Method not allowed on collection. Use /api/orders/[id]" }, { status: 405 });
    } catch (error: any) {
        return NextResponse.json({ error: error.message }, { status: 500 });
    }
}


import { NextResponse } from 'next/server';
import { getDb } from '@/lib/db';

export async function PUT(request: Request, { params }: { params: { id: string } }) {
  try {
    const { id } = await params;
    const body = await request.json();
    const db = await getDb();

    const updates: string[] = [];
    const values: any[] = [];

    if (body.status !== undefined) {
      updates.push("status = ?");
      values.push(body.status);
    }

    if (body.items !== undefined) {
      updates.push("items = ?");
      // If it's already a string (frontend stringified it), use it. 
      // If it's an object/array, stringify it.
      // Ideally frontend sends object, but we handle both.
      const itemsStr = typeof body.items === 'string' ? body.items : JSON.stringify(body.items);
      values.push(itemsStr);
    }

    if (body.total !== undefined) {
      updates.push("value = ?");
      values.push(body.total);
    }

    if (body.value !== undefined) {
      updates.push("value = ?");
      values.push(body.value);
    }

    if (body.payment_method !== undefined) {
      updates.push("payment_method = ?");
      values.push(body.payment_method);
    }

    if (body.name !== undefined) {
      updates.push("name = ?");
      values.push(body.name);
    }

    if (updates.length > 0) {
      const sql = `UPDATE orders SET ${updates.join(', ')} WHERE id = ?`;
      values.push(id);
      await db.run(sql, values);
    }

    // Fetch updated
    const updated = await db.get("SELECT * FROM orders WHERE id = ?", [id]);
    if (updated) {
        try {
            updated.items = JSON.parse(updated.items || '[]');
        } catch (e) {
            updated.items = [];
        }
        updated.total = updated.value;
    }

    return NextResponse.json(updated);
  } catch (error: any) {
    return NextResponse.json({ error: error.message }, { status: 500 });
  }
}

export async function DELETE(request: Request, { params }: { params: { id: string } }) {
    try {
        const { id } = await params;
        const db = await getDb();
        await db.run("DELETE FROM orders WHERE id = ?", [id]);
        return NextResponse.json({ success: true });
    } catch (error: any) {
        return NextResponse.json({ error: error.message }, { status: 500 });
    }
}

export async function GET(request: Request, { params }: { params: { id: string } }) {
    try {
        const { id } = await params;
        const db = await getDb();
        const order = await db.get("SELECT * FROM orders WHERE id = ?", [id]);
        if (order) {
            order.items = JSON.parse(order.items || '[]');
            order.total = order.value;
            return NextResponse.json(order);
        }
        return NextResponse.json({ error: "Order not found" }, { status: 404 });
    } catch (error: any) {
        return NextResponse.json({ error: error.message }, { status: 500 });
    }
}


import { NextResponse } from 'next/server';
import { getDb } from '@/lib/db';

export async function GET(req: Request, { params }: { params: Promise<{ key: string }> }) {
  const { key } = await params;
  const db = await getDb();
  const row = await db.get("SELECT value FROM settings WHERE key = ?", [key]);
  return NextResponse.json({ value: row ? JSON.parse(row.value) : null });
}

export async function POST(req: Request, { params }: { params: Promise<{ key: string }> }) {
  const { key } = await params;
  const { value } = await req.json();
  const db = await getDb();
  await db.run("INSERT OR REPLACE INTO settings (key, value) VALUES (?, ?)", [key, JSON.stringify(value)]);
  return NextResponse.json({ key, value });
}


