"use client"

import { useState, useEffect } from "react"
import { Car, Coffee, Clock, Play, Square, Timer, TrendingUp } from "lucide-react"
import { Card, CardContent } from "@/components/ui/card"
import { Button } from "@/components/ui/button"
import { cn } from "@/lib/utils"

interface StatusCardProps {
  status: "driving" | "resting" | "waiting"
  setStatus: (status: "driving" | "resting" | "waiting") => void
}

const statusConfig = {
  driving: {
    label: "Dirigindo",
    icon: Car,
    color: "text-emerald-500",
    bgColor: "bg-emerald-500/10",
    borderColor: "border-emerald-500/30",
    glowColor: "shadow-emerald-500/20",
  },
  resting: {
    label: "Descansando",
    icon: Coffee,
    color: "text-blue-500",
    bgColor: "bg-blue-500/10",
    borderColor: "border-blue-500/30",
    glowColor: "shadow-blue-500/20",
  },
  waiting: {
    label: "Aguardando",
    icon: Clock,
    color: "text-primary",
    bgColor: "bg-primary/10",
    borderColor: "border-primary/30",
    glowColor: "shadow-primary/20",
  },
}

export function StatusCard({ status, setStatus }: StatusCardProps) {
  const [elapsedTime, setElapsedTime] = useState(0)
  const [tripDistance, setTripDistance] = useState(0)
  const config = statusConfig[status]
  const Icon = config.icon

  useEffect(() => {
    let interval: NodeJS.Timeout
    if (status === "driving") {
      interval = setInterval(() => {
        setElapsedTime((prev) => prev + 1)
        setTripDistance((prev) => prev + Math.random() * 0.5)
      }, 1000)
    }
    return () => clearInterval(interval)
  }, [status])

  const formatTime = (seconds: number) => {
    const hrs = Math.floor(seconds / 3600)
    const mins = Math.floor((seconds % 3600) / 60)
    const secs = seconds % 60
    return `${hrs.toString().padStart(2, "0")}:${mins.toString().padStart(2, "0")}:${secs.toString().padStart(2, "0")}`
  }

  const handleToggleTrip = () => {
    if (status === "driving") {
      setStatus("waiting")
      setElapsedTime(0)
      setTripDistance(0)
    } else {
      setStatus("driving")
    }
  }

  return (
    <Card
      className={cn(
        "mb-6 border-2 transition-all duration-500",
        config.borderColor,
        config.bgColor,
        status === "driving" && "shadow-lg shadow-emerald-500/10",
      )}
    >
      <CardContent className="p-6">
        {/* Status selector pills */}
        <div className="mb-6 flex items-center justify-between">
          <span className="text-sm font-medium text-muted-foreground">Status Atual</span>
          <div className="flex gap-1.5">
            {(["waiting", "driving", "resting"] as const).map((s) => (
              <button
                key={s}
                onClick={() => setStatus(s)}
                className={cn(
                  "h-2.5 w-8 rounded-full transition-all duration-300",
                  status === s
                    ? cn(statusConfig[s].color.replace("text-", "bg-"), "scale-110")
                    : "bg-muted hover:bg-muted-foreground/30",
                )}
              />
            ))}
          </div>
        </div>

        {/* Main status display */}
        <div className="mb-6 flex items-center gap-4">
          <div
            className={cn(
              "relative flex h-16 w-16 items-center justify-center rounded-2xl transition-all duration-500",
              config.bgColor,
            )}
          >
            {status === "driving" && <span className="absolute inset-0 animate-ping rounded-2xl bg-emerald-500/20" />}
            <Icon className={cn("h-8 w-8 transition-all duration-300", config.color)} />
          </div>
          <div className="flex-1">
            <h2 className={cn("text-2xl font-bold transition-colors duration-300", config.color)}>{config.label}</h2>
            <p className="text-sm text-muted-foreground">
              {status === "driving" && "Viagem em andamento"}
              {status === "resting" && "Pausa para descanso"}
              {status === "waiting" && "Pronto para iniciar"}
            </p>
          </div>
        </div>

        {status === "driving" && (
          <div className="mb-6 grid grid-cols-2 gap-3 animate-in fade-in slide-in-from-bottom-2 duration-500">
            <div className="flex items-center gap-3 rounded-xl bg-background/50 p-3">
              <Timer className="h-5 w-5 text-emerald-500" />
              <div>
                <p className="text-xs text-muted-foreground">Tempo</p>
                <p className="font-mono text-lg font-semibold text-foreground">{formatTime(elapsedTime)}</p>
              </div>
            </div>
            <div className="flex items-center gap-3 rounded-xl bg-background/50 p-3">
              <TrendingUp className="h-5 w-5 text-emerald-500" />
              <div>
                <p className="text-xs text-muted-foreground">Distância</p>
                <p className="font-mono text-lg font-semibold text-foreground">{tripDistance.toFixed(1)} km</p>
              </div>
            </div>
          </div>
        )}

        {/* Action button */}
        <Button
          onClick={handleToggleTrip}
          className={cn(
            "h-14 w-full gap-3 text-lg font-semibold transition-all duration-300",
            status === "driving"
              ? "bg-destructive text-destructive-foreground hover:bg-destructive/90"
              : "bg-primary text-primary-foreground hover:bg-primary/90",
          )}
        >
          {status === "driving" ? (
            <>
              <Square className="h-5 w-5" />
              Finalizar Viagem
            </>
          ) : (
            <>
              <Play className="h-5 w-5" />
              Iniciar Nova Viagem
            </>
          )}
        </Button>
      </CardContent>
    </Card>
  )
}


"use client"

import React, { useState, useEffect } from "react"
import { Users, Coffee, Plus, FileText, CheckCircle2, Clock, AlertCircle, CreditCard } from "lucide-react"
import { api } from "@/lib/api"
import { Button } from "@/components/ui/button"
import { Card, CardContent, CardHeader, CardTitle, CardFooter } from "@/components/ui/card"
import { Badge } from "@/components/ui/badge"
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
  DialogFooter,
} from "@/components/ui/dialog"
import { Input } from "@/components/ui/input"
import { Label } from "@/components/ui/label"
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select"
import { ScrollArea } from "@/components/ui/scroll-area"

interface Table {
  id: string
  name: string
  seats: number
}

interface OrderItem {
  id: string
  name: string
  price: number
  quantity: number
}

interface MenuItem {
  id: string
  name: string
  price: number
  category: string
}

interface Order {
  id: string
  table_number: number
  name: string // Customer name
  items: OrderItem[] | string
  status: 'pending' | 'preparing' | 'ready' | 'completed' | 'cancelled'
  total: number
  created_at: string
  payment_method?: string
}

export function TablesBoard() {
  const [tables, setTables] = useState<Table[]>([])
  const [orders, setOrders] = useState<Order[]>([])
  const [menu, setMenu] = useState<MenuItem[]>([])
  const [loading, setLoading] = useState(true)

  // Dialog states
  const [selectedTable, setSelectedTable] = useState<Table | null>(null)
  const [isOrderDialogOpen, setIsOrderDialogOpen] = useState(false)
  const [currentOrder, setCurrentOrder] = useState<Partial<Order> | null>(null)

  // New Order Form
  const [customerName, setCustomerName] = useState("")
  const [selectedItems, setSelectedItems] = useState<OrderItem[]>([])
  const [paymentMethod, setPaymentMethod] = useState<string>("")

  useEffect(() => {
    fetchData()
    const interval = setInterval(fetchData, 5000)
    return () => clearInterval(interval)
  }, [])

  const fetchData = async () => {
    try {
      const [tablesData, ordersData, menuData] = await Promise.all([
        api.get('/tables'),
        api.get('/orders'),
        api.get('/menu')
      ])

      setTables(tablesData)

      // Process orders
      const activeOrders = ordersData
        .map((o: any) => ({
          ...o,
          items: typeof o.items === 'string' ? JSON.parse(o.items) : o.items
        }))
        .filter((o: Order) => o.status !== 'completed' && o.status !== 'cancelled')

      setOrders(activeOrders)
      setMenu(menuData)
      setLoading(false)
    } catch (error) {
      console.error("Error fetching data:", error)
      setLoading(false)
    }
  }

  const getTableOrder = (tableId: string) => {
    // Assuming table names are like "Mesa 1", "Mesa 2". 
    // Ideally we should match by ID, but order stores table_number (int) or string.
    // Let's try to match by parsing table name number or if table stored its ID in order.
    // Current order schema has table_number. Let's assume table.name contains the number.
    const tableNum = parseInt(tables.find(t => t.id === tableId)?.name.replace(/\D/g, '') || '0')
    return orders.find(o => o.table_number === tableNum)
  }

  const handleOpenTable = (table: Table) => {
    setSelectedTable(table)
    setCustomerName("")
    setSelectedItems([])
    setPaymentMethod("")
    setCurrentOrder(null)
    setIsOrderDialogOpen(true)
  }

  const handleViewOrder = (table: Table, order: Order) => {
    setSelectedTable(table)
    setCustomerName(order.name || "")
    setSelectedItems(order.items as OrderItem[])
    setPaymentMethod(order.payment_method || "")
    setCurrentOrder(order)
    setIsOrderDialogOpen(true)
  }

  const addItem = (menuItem: MenuItem) => {
    const existing = selectedItems.find(i => i.id === menuItem.id)
    if (existing) {
      setSelectedItems(selectedItems.map(i => 
        i.id === menuItem.id ? { ...i, quantity: i.quantity + 1 } : i
      ))
    } else {
      setSelectedItems([...selectedItems, {
        id: menuItem.id,
        name: menuItem.name,
        price: Number(menuItem.price),
        quantity: 1
      }])
    }
  }

  const removeItem = (itemId: string) => {
    setSelectedItems(selectedItems.filter(i => i.id !== itemId))
  }

  const updateQuantity = (itemId: string, delta: number) => {
    setSelectedItems(selectedItems.map(i => {
      if (i.id === itemId) {
        const newQty = Math.max(1, i.quantity + delta)
        return { ...i, quantity: newQty }
      }
      return i
    }))
  }

  const calculateTotal = () => {
    return selectedItems.reduce((acc, item) => acc + (item.price * item.quantity), 0)
  }

  const saveOrder = async () => {
    if (!selectedTable) return

    try {
      const tableNum = parseInt(selectedTable.name.replace(/\D/g, '') || '0')
      const orderData = {
        table_number: tableNum,
        name: customerName || `Mesa ${tableNum}`,
        items: JSON.stringify(selectedItems),
        total: calculateTotal(),
        status: currentOrder ? currentOrder.status : 'pending',
        type: 'dine-in',
        payment_method: paymentMethod
      }

      if (currentOrder?.id) {
        await api.put(`/orders/${currentOrder.id}`, orderData)
      } else {
        await api.post('/orders', { ...orderData, id: Date.now().toString() })
      }

      setIsOrderDialogOpen(false)
      fetchData()
    } catch (error) {
      console.error("Failed to save order", error)
    }
  }

  const closeOrder = async () => {
    if (!currentOrder?.id) return

    if (!paymentMethod) {
      alert("Por favor, selecione uma forma de pagamento para fechar a conta.")
      return
    }

    if (confirm("Confirmar fechamento da conta?")) {
      try {
        const finalTotal = calculateTotal()

        await api.put(`/orders/${currentOrder.id}`, { 
          status: 'completed',
          items: JSON.stringify(selectedItems),
          total: finalTotal,
          payment_method: paymentMethod
        })

        // Create transaction
        await api.post('/transactions', {
          id: Date.now().toString(),
          type: 'income',
          description: `Pedido Mesa ${currentOrder.table_number} - ${currentOrder.name} (${paymentMethod})`,
          amount: finalTotal,
          category: 'Vendas',
          date: new Date().toISOString()
        })

        setIsOrderDialogOpen(false)
        fetchData()
      } catch (error) {
        console.error("Failed to close order", error)
      }
    }
  }

  return (
    <div className="space-y-6">
      <div className="flex justify-between items-center">
        <div>
          <h2 className="text-2xl font-bold tracking-tight">Gestão de Mesas</h2>
          <p className="text-muted-foreground">Visão geral e controle de pedidos</p>
        </div>
        <div className="flex gap-4 text-sm">
          <div className="flex items-center gap-2">
            <div className="w-3 h-3 rounded-full bg-green-500"></div>
            <span>Livre</span>
          </div>
          <div className="flex items-center gap-2">
            <div className="w-3 h-3 rounded-full bg-orange-500"></div>
            <span>Ocupada</span>
          </div>
        </div>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6">
        {tables.map(table => {
          const activeOrder = getTableOrder(table.id)
          const isOccupied = !!activeOrder

          return (
            <Card 
              key={table.id} 
              className={`cursor-pointer transition-all hover:shadow-md border-t-4 ${isOccupied ? 'border-t-orange-600 bg-orange-500 dark:bg-orange-900' : 'border-t-green-500'}`}
              onClick={() => isOccupied ? handleViewOrder(table, activeOrder) : handleOpenTable(table)}
            >
              <CardHeader className="pb-2">
                <div className="flex justify-between items-start">
                  <div>
                    <CardTitle className={`text-xl flex items-center gap-2 ${isOccupied ? 'text-white' : ''}`}>
                      {table.name}
                      {isOccupied && activeOrder.name && (
                        <span className="text-sm font-medium text-orange-50 truncate max-w-[120px]">
                          - {activeOrder.name}
                        </span>
                      )}
                    </CardTitle>
                  </div>
                  <Badge variant={isOccupied ? "default" : "secondary"} className={isOccupied ? "bg-white text-orange-600 hover:bg-orange-50" : "bg-green-500 hover:bg-green-600 text-white"}>
                    {isOccupied ? "Ocupada" : "Livre"}
                  </Badge>
                </div>
                <div className={`flex items-center text-sm gap-1 ${isOccupied ? 'text-orange-50' : 'text-muted-foreground'}`}>
                  <Users className="w-4 h-4" />
                  {table.seats} Lugares
                </div>
              </CardHeader>
              <CardContent>
                {isOccupied ? (
                  <div className="space-y-2 mt-2">
                    <div className="flex justify-between text-sm">
                      <span className="text-orange-50">Total:</span>
                      <span className="font-bold text-xl text-white">
                        R$ {(activeOrder.total || 0).toFixed(2)}
                      </span>
                    </div>
                    <div className="flex justify-between text-sm items-center mt-2">
                      <Badge variant="outline" className="text-xs border-orange-200/40 text-white bg-white/20">
                        {(activeOrder.items as any[]).length} itens
                      </Badge>
                      <span className="text-xs text-orange-50 flex items-center gap-1">
                        <Clock className="w-3 h-3" />
                        {new Date(activeOrder.created_at).toLocaleTimeString([], {hour: '2-digit', minute:'2-digit'})}
                      </span>
                    </div>
                  </div>
                ) : (
                  <div className="h-[88px] flex flex-col items-center justify-center text-muted-foreground opacity-50">
                    <Coffee className="w-8 h-8 mb-2" />
                    <span className="text-sm">Toque para abrir</span>
                  </div>
                )}
              </CardContent>
            </Card>
          )
        })}
      </div>

      <Dialog open={isOrderDialogOpen} onOpenChange={setIsOrderDialogOpen}>
        <DialogContent className="max-w-3xl max-h-[90vh] flex flex-col">
          <DialogHeader>
            <DialogTitle>
              {selectedTable?.name} - {currentOrder ? 'Pedido em Andamento' : 'Novo Pedido'}
            </DialogTitle>
          </DialogHeader>

          <div className="flex flex-1 gap-6 overflow-hidden min-h-[400px]">
            {/* Left: Menu Selection */}
            <div className="w-1/2 flex flex-col border-r pr-6">
              <h3 className="font-semibold mb-4 flex items-center gap-2">
                <FileText className="w-4 h-4" /> Cardápio
              </h3>
              <div className="mb-4">
                <Input placeholder="Buscar item..." className="mb-2" />
              </div>
              <ScrollArea className="flex-1 pr-4">
                <div className="space-y-2">
                  {menu.map(item => (
                    <div 
                      key={item.id} 
                      className="flex items-center justify-between p-2 rounded-md border hover:bg-accent cursor-pointer"
                      onClick={() => addItem(item)}
                    >
                      <div>
                        <div className="font-medium text-sm">{item.name}</div>
                        <div className="text-xs text-muted-foreground">R$ {Number(item.price).toFixed(2)}</div>
                      </div>
                      <Button size="icon" variant="ghost" className="h-6 w-6">
                        <Plus className="w-4 h-4" />
                      </Button>
                    </div>
                  ))}
                </div>
              </ScrollArea>
            </div>

            {/* Right: Order Summary */}
            <div className="w-1/2 flex flex-col">
              <div className="space-y-4 mb-4">
                <div>
                  <Label>Nome do Cliente / Identificação</Label>
                  <Input 
                    value={customerName} 
                    onChange={e => setCustomerName(e.target.value)} 
                    placeholder="Ex: João Silva"
                  />
                </div>
                <div>
                  <Label>Forma de Pagamento</Label>
                  <Select value={paymentMethod} onValueChange={setPaymentMethod}>
                    <SelectTrigger>
                      <SelectValue placeholder="Selecione..." />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="Dinheiro">Dinheiro</SelectItem>
                      <SelectItem value="Cartão de Crédito">Cartão de Crédito</SelectItem>
                      <SelectItem value="Cartão de Débito">Cartão de Débito</SelectItem>
                      <SelectItem value="PIX">PIX</SelectItem>
                    </SelectContent>
                  </Select>
                </div>
              </div>

              <div className="flex-1 border rounded-md p-4 flex flex-col">
                <div className="flex-1 overflow-auto space-y-3">
                  {selectedItems.length === 0 ? (
                    <div className="text-center text-muted-foreground py-8">
                      Nenhum item adicionado
                    </div>
                  ) : (
                    selectedItems.map(item => (
                      <div key={item.id} className="flex justify-between items-center text-sm">
                        <div className="flex-1">
                          <span className="font-medium">{item.quantity}x</span> {item.name}
                        </div>
                        <div className="flex items-center gap-2">
                          <span className="text-muted-foreground">R$ {(item.price * item.quantity).toFixed(2)}</span>
                          <div className="flex items-center gap-1">
                            <button 
                              className="w-5 h-5 flex items-center justify-center bg-secondary rounded hover:bg-secondary/80"
                              onClick={() => updateQuantity(item.id, -1)}
                            >
                              -
                            </button>
                            <button 
                              className="w-5 h-5 flex items-center justify-center bg-secondary rounded hover:bg-secondary/80"
                              onClick={() => updateQuantity(item.id, 1)}
                            >
                              +
                            </button>
                            <button 
                              className="w-5 h-5 flex items-center justify-center text-red-500 hover:bg-red-50 rounded"
                              onClick={() => removeItem(item.id)}
                            >
                              x
                            </button>
                          </div>
                        </div>
                      </div>
                    ))
                  )}
                </div>

                <div className="mt-4 pt-4 border-t space-y-2">
                  <div className="flex justify-between items-center text-lg font-bold">
                    <span>Total</span>
                    <span>R$ {calculateTotal().toFixed(2)}</span>
                  </div>
                </div>
              </div>
            </div>
          </div>

          <DialogFooter className="mt-4 gap-2">
            {currentOrder && (
              <Button variant="destructive" onClick={closeOrder} className="mr-auto">
                <CheckCircle2 className="w-4 h-4 mr-2" />
                Fechar Conta
              </Button>
            )}
            <Button variant="outline" onClick={() => setIsOrderDialogOpen(false)}>
              Cancelar
            </Button>
            <Button onClick={saveOrder} disabled={selectedItems.length === 0}>
              {currentOrder ? 'Atualizar Pedido' : 'Abrir Mesa'}
            </Button>
          </DialogFooter>
        </DialogContent>
      </Dialog>
    </div>
  )
}


